import React, { useEffect, useState, useRef } from 'react';
import Section from '../common/Section';
import Button from '../common/Button';
import './Partners.css';

const Partners = () => {
  const [logos, setLogos] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);
  const [isReset, setIsReset] = useState(false);
  const containerRef = useRef(null);
  const timeoutRef = useRef(null);
  const delay = 3000; // 3 second delay between slides
  
  useEffect(() => {
    // Dynamically import all logo files from the directory
    const importAllLogos = () => {
      let logoContext;
      try {
        // For webpack environments
        logoContext = require.context('../../../public/img/logos', false, /\.(png|jpe?g|svg)$/);
        return logoContext.keys().map(logoContext);
      } catch (error) {
        console.error('Error loading logos:', error);
        
        // Fallback to some sample logos if require.context is not available
        return [
          '/img/logos/logo1.png',
          '/img/logos/logo2.png',
          '/img/logos/logo3.png',
        ].map(path => ({ default: path }));
      }
    };
    
    const importedLogos = importAllLogos();
    // Extract the file paths from the imported modules
    const logoPaths = importedLogos.map(module => 
      typeof module === 'string' ? module : module.default
    );
    
    setLogos(logoPaths);
    
    // Start the automatic slideshow
    startSlideshow();

    // Cleanup function
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [logos]);

  // Start the slideshow timer
  const startSlideshow = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    
    timeoutRef.current = setTimeout(() => {
      if (activeIndex === logos.length - 1) {
        // If we're at the last logo, reset to the first one
        // with a special flag to handle the transition
        setIsReset(true);
        setActiveIndex(0);
        
        // Remove the reset flag after the position is reset
        setTimeout(() => {
          setIsReset(false);
        }, 50);
      } else {
        // Move to the next logo
        setActiveIndex(prevIndex => prevIndex + 1);
      }
      
      // Continue the slideshow
      startSlideshow();
    }, delay);
  };

  // Reset slideshow on mouse interaction
  const resetTimer = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
      startSlideshow();
    }
  };

  // Move to a specific logo
  const goToLogo = (index) => {
    setActiveIndex(index);
    resetTimer();
  };

  // Get additional logos for seamless looping
  const getVisibleLogos = () => {
    if (logos.length === 0) return [];
    
    // Create an array with the current set of logos plus copies for infinite scrolling
    const prev = (activeIndex - 1 + logos.length) % logos.length;
    const next = (activeIndex + 1) % logos.length;
    
    return [
      { logo: logos[prev], position: 'prev', index: prev },
      { logo: logos[activeIndex], position: 'active', index: activeIndex },
      { logo: logos[next], position: 'next', index: next },
    ];
  };

  return (
    <section className="partners">
      <div className='container'>
        <h3 className='section-title'>С нашими резюме люди устраиваются в лучшие компании</h3>
      </div>
      <div className="logo-carousel">
        <div className={`logo-container ${isReset ? 'no-transition' : ''}`} ref={containerRef}>
          {getVisibleLogos().map((item, idx) => (
            <div 
              key={`visible-logo-${idx}`} 
              className={`logo ${item.position}`}
              onClick={() => goToLogo(item.index)}
            >
              <img src={item.logo} alt={`Partner logo ${item.index + 1}`} />
            </div>
          ))}
        </div>
        
        <div className="carousel-indicators">
          {logos.map((_, index) => (
            <button 
              key={`indicator-${index}`}
              className={`indicator ${index === activeIndex ? 'active' : ''}`}
              onClick={() => goToLogo(index)}
              aria-label={`Go to logo ${index + 1}`}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Partners;
