import React, { useRef, useState } from 'react';
import './SaveSection.css';

const SaveSection = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);
  const containerRef = useRef(null);
  
  // Sample saving benefits data
const savingBenefits = [
    {
        id: 1,
        title: "5 000 – 10 000 ₽",
        description: "Именно столько стоит работа профессиональных консультантов, которую Yoffer сделает для вас за 15 минут бесплатно!",
    },
    {
        id: 2,
        title: "1-3 месяца",
        description: "Сколько времени вы можете сэкономить, если будете искать работу с нами",
    },
    {
        id: 3,
        title: "от 1,5 часов",
        description: "Сколько времени вы тратите на поиск работы, если будете искать работу с нами",
    }
];

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX - containerRef.current.offsetLeft);
    setScrollLeft(containerRef.current.scrollLeft);
  };

  const handleTouchStart = (e) => {
    setIsDragging(true);
    setStartX(e.touches[0].pageX - containerRef.current.offsetLeft);
    setScrollLeft(containerRef.current.scrollLeft);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const x = e.pageX - containerRef.current.offsetLeft;
    const walk = (x - startX) * 2; // Scroll speed multiplier
    
    // Apply manual transform during drag
    containerRef.current.style.transform = `translateX(${walk}px)`;
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const x = e.touches[0].pageX - containerRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    
    containerRef.current.style.transform = `translateX(${walk}px)`;
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    // Reset transform to let the animation take over again
    setTimeout(() => {
      if (containerRef.current) {
        containerRef.current.style.transform = '';
      }
    }, 100);
  };

  const handleMouseLeave = () => {
    if (isDragging) {
      handleMouseUp();
    }
  };

  return (
    <section className="save-container">
      <div className="container">
        <h3 className="section-title"><span className='accent'>Yoffer</span> экономит ваше время и деньги</h3>
      </div>
      
      <div className="cards-flex"
           onMouseDown={handleMouseDown}
           onMouseMove={handleMouseMove}
           onMouseUp={handleMouseUp}
           onMouseLeave={handleMouseLeave}
           onTouchStart={handleTouchStart}
           onTouchMove={handleTouchMove}
           onTouchEnd={handleMouseUp}>
        <div className={`cards-container ${isDragging ? 'dragging' : ''}`}
             ref={containerRef}>
          {/* First set of cards */}
          {savingBenefits.map((benefit) => (
            <div key={`benefit-${benefit.id}`} className="save-card">
              <p>{benefit.title}</p>
              <p className='save-card-description'>{benefit.description}</p>
              <div className='save-icon'>
                <img src="/img/svg/save.svg" alt="" />
              </div>
            </div>
          ))}
          
          {/* Duplicate set for seamless infinite scrolling */}
          {savingBenefits.map((benefit) => (
            <div key={`benefit-dup-${benefit.id}`} className="save-card">
              <p>{benefit.title}</p>
              <p className='save-card-description'>{benefit.description}</p>
              <div className='save-icon'>
                <img src="/img/svg/save.svg" alt="" />
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default SaveSection;
