          title: "/img/testimonials/avatar_elena.jpg",
          text_before: "–æ–±—Å—Ç–∞–Ω–æ–≤–∫–∞ –Ω–∞ —Ä–∞–±–æ—Ç–µ –ø—Ä–æ—Å—Ç–æ —É–∂–∞—Å. –ö–∞–∂–¥—ã–π –¥–µ–Ω—å –¥—É–º–∞–ª–∞ ¬´—É–≤–æ–ª—é—Å—å¬ª. –ù–æ —á—Ç–æ –ø–∏—Å–∞—Ç—å –≤ —Ä–µ–∑—é–º–µ? –ö–æ–º—É —è –Ω—É–∂–Ω–∞?! –ï—Å–ª–∏ –±—ã –Ω–µ Yoffer, —Ç–∞–∫ –∏ —Å–∏–¥–µ–ª–∞ –±—ã —Ç–∞–º",
          text_after: "—è, —á–µ—Å—Ç–Ω–æ –≥–æ–≤–æ—Ä—è, –æ—Ñ–∏–≥–µ–ª–∞ –æ—Ç —Ç–æ–≥–æ, –∫–∞–∫ –º–µ–Ω—è –æ–ø–∏—Å–∞–ª –∞–ª–≥–æ—Ä–∏—Ç–º! –°–∞–º–∞ –±—ã —Ç–∞–∫–æ–≥–æ —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–∞ –Ω–∞–Ω—è–ª–∞ –∑–∞ –¥–≤–æ–π–Ω—É—é –ø–ª–∞—Ç—É) –ò—Ç–æ–≥ ‚Äî —Ä–∞–±–æ—Ç–∞—é —Å–µ–π—á–∞—Å –Ω–∞ –Ω–æ–≤–æ–º –º–µ—Å—Ç–µ, –≥–¥–µ –º–µ–Ω—è —Ü–µ–Ω—è—Ç, —É–≤–∞–∂–∞—é—Ç –∏ –ø–ª–∞—Ç—è—Ç –≤ 1,5 —Ä–∞–∑–∞ –±–æ–ª—å—à–µ"
        },
        {
          id: 4,
          name: "–ê–Ω–¥—Ä–µ–π",
          position: "–ö–æ–º–º–µ—Ä—á–µ—Å–∫–∏–π –¥–∏—Ä–µ–∫—Ç–æ—Ä",
          title: "/img/testimonials/avatar_andrey.jpg",
          text_before: "–ø—Ä–µ—Ç–µ–Ω–¥–æ–≤–∞–ª –Ω–∞ –ø–æ–∑–∏—Ü–∏—é –∫–æ–º–º–µ—Ä—á–µ—Å–∫–æ–≥–æ –¥–∏—Ä–µ–∫—Ç–æ—Ä–∞. –°–∞–º–æ—Å—Ç–æ—è—Ç–µ–ª—å–Ω–æ –∏—Å–∫–∞–ª –æ–∫–æ–ª–æ –ø–æ–ª—É–≥–æ–¥–∞. –û—Ç–∫–ª–∏–∫–æ–≤ –ø–æ—á—Ç–∏ –Ω–æ–ª—å",
          text_after: "–∞–ª–≥–æ—Ä–∏—Ç–º –æ—Ç–ª–∏—á–Ω–æ —É–ø–∞–∫–æ–≤–∞–ª –º–æ–∏ –∑–Ω–∞–Ω–∏—è –∏ –Ω–∞–≤—ã–∫–∏. –¢–∞–∫ —á—Ç–æ —è –¥–∞–∂–µ —Ä–µ—à–∏–ª –ø–æ–≤—ã—Å–∏—Ç—å –ø–ª–∞–Ω–∫—É –∑–∞—Ä–ø–ª–∞—Ç–Ω—ã—Ö –æ–∂–∏–¥–∞–Ω–∏–π –∏, –∫–∞–∫ –Ω–∏ —Å—Ç—Ä–∞–Ω–Ω–æ, –Ω–∞—à–µ–ª –Ω–æ–≤—É—é —Ä–∞–±–æ—Ç—É, –≥–¥–µ –º–Ω–µ –≥–æ—Ç–æ–≤—ã —Å—Ç–æ–ª—å–∫–æ –ø–ª–∞—Ç–∏—Ç—å"
        },
        {
          id: 5,
          name: "–ö—Å–µ–Ω–∏—è",
          position: "SMM-—Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç",
          title: "/img/testimonials/avatar_ksenia.jpg",
          text_before: "–º–µ–Ω—è –ø–æ—Å—Ç–æ—è–Ω–Ω–æ –ø–µ—Ä–µ–≥—Ä—É–∂–∞–ª–∏ –∏ –ø—Ä–∏ —ç—Ç–æ–º –Ω–µ –¥–æ–ø–ª–∞—á–∏–≤–∞–ª–∏ –∑–∞ —ç—Ç–æ. –Ø —Ä–µ—à–∏–ª–∞ –Ω–∞–π—Ç–∏ –Ω–æ–≤—É—é —Ä–∞–±–æ—Ç—É",
          text_after: "–ø–æ–ª—É—á–∏–ª–æ—Å—å –∫–ª–∞—Å—Å–Ω–æ–µ —Ä–µ–∑—é–º–µ. –£–∂–µ —á–µ—Ä–µ–∑ 1,5 –Ω–µ–¥–µ–ª–∏ —É –º–µ–Ω—è –±—ã–ª –æ—Ñ–µ—Ä –æ—Ç –Ω–æ–≤–æ–≥–æ —Ä–∞–±–æ—Ç–æ–¥–∞—Ç–µ–ª—è. –ü–æ—à–ª–∞ —É–≤–æ–ª—å–Ω—è—Ç—å—Å—è, –Ω–æ –Ω–∞—á–∞–ª—å–Ω–∏–∫ –º–µ–Ω—è –Ω–µ –æ—Ç–ø—É—Å—Ç–∏–ª. –°–∫–∞–∑–∞–ª, —á—Ç–æ –±–µ–∑ –º–µ–Ω—è —Ç—É—Ç –≤—Å–µ —Ä—É—Ö–Ω–µ—Ç. –ü–æ–¥–Ω—è–ª –º–Ω–µ –∑–∞—Ä–ø–ª–∞—Ç—É –∏ –±–æ–ª—å—à–µ –Ω–µ –¥–µ—Ä–≥–∞–µ—Ç –ø–æ –≤—ã—Ö–æ–¥–Ω—ã–º"
        }
      
      
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [touchStart, setTouchStart] = useState(0);
  const [touchEnd, setTouchEnd] = useState(0);
  const [isTouching, setIsTouching] = useState(false);
  const [translateX, setTranslateX] = useState(0);
  const sliderRef = useRef(null);

  const handleTouchStart = (e) => {
    setTouchStart(e.targetTouches[0].clientX);
    setIsTouching(true);
  };

  const handleTouchMove = (e) => {
    if (!isTouching) return;
    setTouchEnd(e.targetTouches[0].clientX);
    
    const diff = touchStart - e.targetTouches[0].clientX;
    const slideWidth = sliderRef.current?.offsetWidth || 0;
    
    // Calculate how far we've moved during the touch
    const newTranslate = -(currentIndex * 100) - (diff / slideWidth * 100);
    setTranslateX(newTranslate);
  };

  const handleTouchEnd = () => {
    setIsTouching(false);
    
    // Reset translateX to snap back to proper position
    setTranslateX(-(currentIndex * 100));
    
    if (touchStart - touchEnd > 75) {
      // Swipe left - go to next slide
      if (currentIndex < testimonials.length - 1) {
        setCurrentIndex(currentIndex + 1);
      }
    }
    
    if (touchStart - touchEnd < -75) {
      // Swipe right - go to previous slide
      if (currentIndex > 0) {
        setCurrentIndex(currentIndex - 1);
      }
    }
  };

  // Update translateX whenever currentIndex changes
  useEffect(() => {
    setTranslateX(-(currentIndex * 100));
  }, [currentIndex]);

  // Function to go to next slide
  const nextSlide = () => {
    if (currentIndex < testimonials.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  // Function to go to previous slide
  const prevSlide = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  return (
    <div className="testimonials-section">
      <div className="container">
        <h3 className="section-title">–û—Ç–∑—ã–≤—ã –Ω–∞—à–∏—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π</h3>
        
        <div className="testimonials-slider-container" ref={sliderRef}>
          <div 
            className="testimonials-slider"
            style={{ 
              transform: `translateX(${isTouching ? translateX : -(currentIndex * 100)}%)`,
              transition: isTouching ? 'none' : 'transform 0.3s ease-out'
            }}
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
          >
            {testimonials.map((testimonial) => (
              <div key={testimonial.id} className="testimonial-slide">
                <div className="testimonial-card">
                  <div className="testimonial-header">
                   
                    <div className="testimonial-author">
                        
                      <h4>{testimonial.name}</h4>
                      
                      <p className="testimonial-position">{testimonial.position}</p>
                    </div>
                    
                  </div>
                  <div className="testimonial-title">
                        <p className="testimonial-title">{testimonial.title}</p>
                    </div>
                  <p className="testimonial-text"><strong>–î–æ: </strong>{testimonial.text_before}</p>
                  <p className="testimonial-text"><strong>–ü–æ—Å–ª–µ: </strong>{testimonial.text_after}</p>
                </div>
              </div>
            ))}
          </div>
          
          
        </div>
      </div>
    </div>
  );
};

export default TestimonialsSection;
