import React, { useEffect, useState, useRef } from 'react';
import Section from '../common/Section';
import Button from '../common/Button';
import './Partners.css';

const Partners = () => {
  const [logos, setLogos] = useState([]);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [currentTranslate, setCurrentTranslate] = useState(0);
  const containerRef = useRef(null);
  
  useEffect(() => {
    // Dynamically import all logo files from the directory
    const importAllLogos = () => {
      let logoContext;
      try {
        // For webpack environments
        logoContext = require.context('../../../public/img/logos', false, /\.(png|jpe?g|svg)$/);
        return logoContext.keys().map(logoContext);
      } catch (error) {
        console.error('Error loading logos:', error);
        
        // Fallback to some sample logos if require.context is not available
        return [
          '/img/logos/logo1.png',
          '/img/logos/logo2.png',
          '/img/logos/logo3.png',
        ].map(path => ({ default: path }));
      }
    };
    
    const importedLogos = importAllLogos();
    // Extract the file paths from the imported modules
    const logoPaths = importedLogos.map(module => 
      typeof module === 'string' ? module : module.default
    );
    
    setLogos(logoPaths);
  }, []);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX);
    if (containerRef.current) {
      // Stop the animation immediately
      containerRef.current.style.animationPlayState = 'paused';
      
      // Get current translation from computed style
      const computedStyle = window.getComputedStyle(containerRef.current);
      const matrix = new WebKitCSSMatrix(computedStyle.transform);
      setCurrentTranslate(matrix.m41); // m41 is the translateX value
    }
  };

  const handleTouchStart = (e) => {
    e.preventDefault(); // Prevent scrolling while dragging
    setIsDragging(true);
    setStartX(e.touches[0].pageX);
    if (containerRef.current) {
      containerRef.current.style.animationPlayState = 'paused';
      
      const computedStyle = window.getComputedStyle(containerRef.current);
      const matrix = new WebKitCSSMatrix(computedStyle.transform);
      setCurrentTranslate(matrix.m41);
    }
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    
    const currentX = e.pageX;
    const diff = currentX - startX;
    
    if (containerRef.current) {
      containerRef.current.style.transform = `translateX(${currentTranslate + diff}px)`;
    }
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    
    const currentX = e.touches[0].pageX;
    const diff = currentX - startX;
    
    if (containerRef.current) {
      containerRef.current.style.transform = `translateX(${currentTranslate + diff}px)`;
    }
  };

  const handleMouseUp = () => {
    if (!isDragging) return;
    setIsDragging(false);
    
    if (containerRef.current) {
      // Store the current position
      const finalTranslate = currentTranslate + (event.pageX - startX);
      
      // Reset to CSS animation with a transition
      containerRef.current.style.transition = 'none';
      containerRef.current.style.transform = '';
      containerRef.current.style.animationPlayState = 'running';
      
      // Force a reflow to apply changes
      containerRef.current.offsetHeight;
      
      // Remove the transition after it's done
      setTimeout(() => {
        if (containerRef.current) {
          containerRef.current.style.transition = '';
        }
      }, 50);
    }
  };

  const handleMouseLeave = (e) => {
    if (isDragging) {
      handleMouseUp(e);
    }
  };

  return (
    <section className="partners">
      <div className='container'>
        <h3 className='section-title'> С нашими резюме люди устраиваются в лучшие компании</h3>
      </div>
      <div className="logo-flex"
           onMouseDown={handleMouseDown}
           onMouseMove={handleMouseMove}
           onMouseUp={handleMouseUp}
           onMouseLeave={handleMouseLeave}
           onTouchStart={handleTouchStart}
           onTouchMove={handleTouchMove}
           onTouchEnd={handleMouseUp}>
        <div className={`logo-container ${isDragging ? 'dragging' : ''}`}
             ref={containerRef}>
          {/* First set of logos */}
          {logos.map((logo, index) => (
            <div key={`logo-${index}`} className="logo">
              <img src={logo} alt={`Partner logo ${index + 1}`} />
            </div>
          ))}
          
          {/* Duplicate set for seamless infinite scrolling */}
          {logos.map((logo, index) => (
            <div key={`logo-dup-${index}`} className="logo">
              <img src={logo} alt={`Partner logo ${index + 1}`} />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Partners;
