import React, { useRef, useState, useEffect } from 'react';
import './Algorithm.css';
import Section from '../common/Section';
import Button from '../common/Button';

const Algorithm = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);
  const [isDesktop, setIsDesktop] = useState(false);
  const containerRef = useRef(null);
  const animationRef = useRef(null);
  const speedRef = useRef(0.5); // Speed of auto scrolling
  
  // Check if we're on desktop
  useEffect(() => {
    const checkIfDesktop = () => {
      setIsDesktop(window.innerWidth >= 1024);
    };
    
    checkIfDesktop();
    window.addEventListener('resize', checkIfDesktop);
    
    return () => window.removeEventListener('resize', checkIfDesktop);
  }, []);
  
  // Setup and manage the infinite slider
  useEffect(() => {
    if (isDesktop || !containerRef.current) return;
    
    const container = containerRef.current;
    let scrollPosition = 0;
    
    // Clone first set of cards if needed for smooth scrolling
    const firstCardSet = container.querySelectorAll('.algorithm-card');
    const cardSetWidth = Array.from(firstCardSet).reduce(
      (width, card) => width + card.offsetWidth + 8, // 8px is the gap between cards
      0
    );
    
    // Function to scroll the slider
    const scrollSlider = () => {
      if (!container || isDesktop) return;
      
      scrollPosition += speedRef.current;
      
      // Reset position when reaching the end of a set
      if (scrollPosition >= cardSetWidth) {
        scrollPosition = 0;
      }
      
      container.style.transform = `translateX(-${scrollPosition}px)`;
      animationRef.current = requestAnimationFrame(scrollSlider);
    };
    
    // Start the animation
    animationRef.current = requestAnimationFrame(scrollSlider);
    
    // Cleanup function
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [isDesktop]);
  
  // Sample algorithm steps data
  const algorithmSteps = [
    {
      id: 1,
      title: "Создаст персональное резюме",
    },
    {
      id: 2,
      title: "Выделит ваши сильные стороны",
    },
    {
      id: 3,
      title: "Привлечет работодателей уже через несколько часов",
    }
  ];

  const handleMouseDown = (e) => {
    if (isDesktop) return;
    setIsDragging(true);
    setStartX(e.pageX - containerRef.current.offsetLeft);
    setScrollLeft(containerRef.current.scrollLeft);
    
    // Pause the animation while dragging
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }
  };

  const handleTouchStart = (e) => {
    if (isDesktop) return;
    setIsDragging(true);
    setStartX(e.touches[0].pageX - containerRef.current.offsetLeft);
    setScrollLeft(containerRef.current.scrollLeft);
    
    // Pause the animation while dragging
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }
  };

  const handleMouseMove = (e) => {
    if (!isDragging || isDesktop) return;
    e.preventDefault();
    const x = e.pageX - containerRef.current.offsetLeft;
    const walk = (x - startX) * 2; // Scroll speed multiplier
    
    // Apply manual transform during drag
    containerRef.current.style.transform = `translateX(${walk}px)`;
  };

  const handleTouchMove = (e) => {
    if (!isDragging || isDesktop) return;
    const x = e.touches[0].pageX - containerRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    
    containerRef.current.style.transform = `translateX(${walk}px)`;
  };

  const handleMouseUp = () => {
    if (isDesktop) return;
    setIsDragging(false);
    
    // Resume the animation
    const startAnimation = () => {
      let currentPosition = 0;
      const transform = containerRef.current?.style.transform;
      if (transform) {
        const match = transform.match(/translateX\(([-\d.]+)px\)/);
        if (match && match[1]) {
          currentPosition = parseFloat(match[1]);
        }
      }
      
      const scrollSlider = () => {
        if (!containerRef.current || isDesktop) return;
        
        currentPosition -= speedRef.current;
        containerRef.current.style.transform = `translateX(${currentPosition}px)`;
        
        animationRef.current = requestAnimationFrame(scrollSlider);
      };
      
      animationRef.current = requestAnimationFrame(scrollSlider);
    };
    
    setTimeout(startAnimation, 100);
  };

  const handleMouseLeave = () => {
    if (isDragging && !isDesktop) {
      handleMouseUp();
    }
  };

  return (
    <section className="algorithm-container">
      <div className="container">
        <h3 className="section-title accent">Алгоритм обучен профессиональными рекрутерами и HR</h3>
      
      
      <div className="cards-flex"
           onMouseDown={handleMouseDown}
           onMouseMove={handleMouseMove}
           onMouseUp={handleMouseUp}
           onMouseLeave={handleMouseLeave}
           onTouchStart={handleTouchStart}
           onTouchMove={handleTouchMove}
           onTouchEnd={handleMouseUp}>
        <div className={`cards-container ${isDragging ? 'dragging' : ''}`}
             ref={containerRef}>
          {/* Create multiple sets for infinite scrolling */}
          {algorithmSteps.map((step) => (
            <div key={`step-${step.id}`} className="algorithm-card">
              <p>{step.title}</p>
              <div className='like'>
                <img src="/img/svg/like.svg" alt="" />
              </div>
            </div>
          ))}
          
          {/* Duplicate sets for seamless infinite scrolling - show on mobile */}
          {!isDesktop && [...Array(3)].flatMap((_, index) => 
            algorithmSteps.map((step) => (
              <div key={`step-dup-${index}-${step.id}`} className="algorithm-card">
                <p>{step.title}</p>
                <div className='like'>
                  <img src="/img/svg/like.svg" alt="" />
                </div>
              </div>
            ))
          )}
        </div>
      </div>
      </div>
    </section>
  );
};

export default Algorithm;
