import React, { useEffect, useState, useRef } from 'react';
import Section from '../common/Section';
import Button from '../common/Button';
import './Partners.css';

const Partners = () => {
  const [logos, setLogos] = useState([]);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);
  const sliderRef = useRef(null);
  const isTransitioning = useRef(false);
  
  useEffect(() => {
    // Dynamically import all logo files from the directory
    const importAllLogos = () => {
      let logoContext;
      try {
        // For webpack environments
        logoContext = require.context('../../../public/img/logos', false, /\.(png|jpe?g|svg)$/);
        return logoContext.keys().map(logoContext);
      } catch (error) {
        console.error('Error loading logos:', error);
        
        // Fallback to some sample logos if require.context is not available
        return [
          '/img/logos/logo1.png',
          '/img/logos/logo2.png',
          '/img/logos/logo3.png',
        ].map(path => ({ default: path }));
      }
    };
    
    const importedLogos = importAllLogos();
    // Extract the file paths from the imported modules
    const logoPaths = importedLogos.map(module => 
      typeof module === 'string' ? module : module.default
    );
    
    setLogos(logoPaths);
  }, []);

  const handleNextSlide = () => {
    if (logos.length === 0 || isTransitioning.current) return;
    
    if (currentSlide === logos.length - 1) {
      // When we're at the last slide, prepare for seamless transition
      isTransitioning.current = true;
      setCurrentSlide(currentSlide + 1); // Move to the clone of the first slide
      
      // After animation completes, immediately jump back to the first real slide without animation
      setTimeout(() => {
        document.querySelector('.logo-container').style.transition = 'none';
        setCurrentSlide(0);
        
        // Re-enable transitions after the DOM has updated
        setTimeout(() => {
          document.querySelector('.logo-container').style.transition = 'transform 0.5s ease-out';
          isTransitioning.current = false;
        }, 50);
      }, 500); // Match this to your transition duration
    } else {
      setCurrentSlide(prev => prev + 1);
    }
  };

  const handlePrevSlide = () => {
    if (logos.length === 0 || isTransitioning.current) return;
    
    if (currentSlide === 0) {
      // When we're at the first slide, prepare for seamless transition
      isTransitioning.current = true;
      setCurrentSlide(-1); // Move to the clone of the last slide
      
      // After animation completes, immediately jump to the last real slide without animation
      setTimeout(() => {
        document.querySelector('.logo-container').style.transition = 'none';
        setCurrentSlide(logos.length - 1);
        
        // Re-enable transitions after the DOM has updated
        setTimeout(() => {
          document.querySelector('.logo-container').style.transition = 'transform 0.5s ease-out';
          isTransitioning.current = false;
        }, 50);
      }, 500); // Match this to your transition duration
    } else {
      setCurrentSlide(prev => prev - 1);
    }
  };

  const handleMouseDown = (e) => {
    if (!sliderRef.current) return;
    setIsDragging(true);
    setStartX(e.pageX - sliderRef.current.offsetLeft);
    setScrollLeft(sliderRef.current.scrollLeft);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleMouseMove = (e) => {
    if (!isDragging || !sliderRef.current) return;
    e.preventDefault();
    const x = e.pageX - sliderRef.current.offsetLeft;
    const walk = (x - startX) * 2; // Scroll speed multiplier
    
    // Determine direction of swipe
    if (walk < -50) {
      handleNextSlide();
      setIsDragging(false);
    } else if (walk > 50) {
      handlePrevSlide();
      setIsDragging(false);
    }
  };

  // Touch events for mobile
  const handleTouchStart = (e) => {
    if (!sliderRef.current) return;
    setIsDragging(true);
    setStartX(e.touches[0].pageX - sliderRef.current.offsetLeft);
  };

  const handleTouchMove = (e) => {
    if (!isDragging || !sliderRef.current) return;
    const x = e.touches[0].pageX - sliderRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    
    if (walk < -50) {
      handleNextSlide();
      setIsDragging(false);
    } else if (walk > 50) {
      handlePrevSlide();
      setIsDragging(false);
    }
  };

  // Get slide width for calculations
  const getSlideWidth = () => {
    // For larger screens use 172px, for mobile use 140px
    return window.innerWidth <= 768 ? 160 : 192; // 140px + 2*10px margin = 160px
  };

  return (
    <section className="partners">
      <div className='container'>
        <h3 className='section-title'>С нашими резюме люди устраиваются в лучшие компании</h3>
      </div>
      <div className="slider-container">
        <button className="slider-nav prev" onClick={handlePrevSlide}>
          &lt;
        </button>
        
        <div 
          className="logo-flex"
          ref={sliderRef}
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
          onMouseMove={handleMouseMove}
          onTouchStart={handleTouchStart}
          onTouchEnd={handleMouseUp}
          onTouchMove={handleTouchMove}
        >
          <div 
            className={`logo-container ${isDragging ? 'dragging' : ''}`}
            style={{ transform: `translateX(-${currentSlide * getSlideWidth()}px)` }}
          ></div>
            {/* Clone of the last slide for infinite effect when going backwards */}
            {logos.length > 0 && (
              <div 
                key="logo-clone-last" 
                className={`logo ${currentSlide === -1 ? 'active' : ''}`}
              >
                <img src={logos[logos.length - 1]} alt="Partner logo" />
              </div>
            )}
            
            {/* Main slides */}
            {logos.map((logo, index) => (
              <div 
                key={`logo-${index}`} 
                className={`logo ${currentSlide === index ? 'active' : ''}`}
              >
                <img src={logo} alt={`Partner logo ${index + 1}`} />
              </div>
            ))}
            
            {/* Clone of the first slide for infinite effect when going forward */}
            {logos.length > 0 && (
              <div 
                key="logo-clone-first" 
                className={`logo ${currentSlide === logos.length ? 'active' : ''}`}
              >
                <img src={logos[0]} alt="Partner logo" />
              </div>
            )}
          </div>
        
      
      <div className="slider-dots"></div>
        {logos.map((_, index) => (
          <span 
            key={`dot-${index}`}
            className={`dot ${currentSlide === index ? 'active' : ''}`}
            onClick={() => setCurrentSlide(index)}
          ></span>
        ))}
      </div>
    </section>
  );
};

export default Partners;
