import React, { Suspense, lazy } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Header from './components/layout/Header';
import Footer from './components/layout/Footer';
import ErrorBoundary from './components/common/ErrorBoundary';
import LoadingSpinner from './components/common/LoadingSpinner';

// Lazy load sections for better performance
const HeroSection = lazy(() => import('./components/sections/Hero'));
const FeaturesSection = lazy(() => import('./components/sections/Features'));
const AboutSection = lazy(() => import('./components/sections/About'));
const ContactSection = lazy(() => import('./components/sections/Contact'));

// Main content component with error boundaries
const MainContent = () => (
  <ErrorBoundary fallback={<div className="error-container">Something went wrong. Please try again later.</div>}>
    <main className="main-content">
      <section id="home" className="section-container">
        <Suspense fallback={<LoadingSpinner />}>
          <HeroSection />
        </Suspense>
      </section>
      
      <section id="features" className="section-container">
        <Suspense fallback={<LoadingSpinner />}>
          <FeaturesSection />
        </Suspense>
      </section>
      
      
    </main>
  </ErrorBoundary>
);

function App() {
  return (
    <Router>
      <div className="app">
        <Header />
        <Routes>
          <Route path="/" element={<MainContent />} />
          <Route path="/features" element={
            <ErrorBoundary>
              <Suspense fallback={<LoadingSpinner />}>
                <FeaturesSection />
              </Suspense>
            </ErrorBoundary>
          } />
          <Route path="/about" element={
            <ErrorBoundary>
              <Suspense fallback={<LoadingSpinner />}>
                <AboutSection />
              </Suspense>
            </ErrorBoundary>
          } />
          <Route path="/contact" element={
            <ErrorBoundary>
              <Suspense fallback={<LoadingSpinner />}>
                <ContactSection />
              </Suspense>
            </ErrorBoundary>
          } />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
