import React, { useState, useRef, useEffect } from 'react';
import './TestimonialsSection.css';

const TestimonialsSection = () => {
  // Sample testimonials data
  const testimonials = [
    {
      id: 1,
      name: "Александр К.",
      position: "Разработчик ПО",
      text: "Благодаря Yoffer я получил работу в компании своей мечты. Алгоритм точно подобрал релевантные ключевые слова, и моё резюме не потерялось в общем потоке.",
      avatar: "/img/testimonials/avatar1.jpg"
    },
    {
      id: 2,
      name: "Екатерина М.",
      position: "Маркетолог",
      text: "Создала персонализированное резюме для каждой вакансии, на которую откликалась. В результате получила три приглашения на собеседование из пяти откликов!",
      avatar: "/img/testimonials/avatar2.jpg"
    },
    {
      id: 3,
      name: "Дмитрий П.",
      position: "Начинающий аналитик",
      text: "У меня был минимальный опыт работы, но Yoffer помог структурировать мои навыки так, что работодатель увидел мой потенциал. Теперь я работаю в крупной компании.",
      avatar: "/img/testimonials/avatar3.jpg"
    },
    {
      id: 4,
      name: "Ольга В.",
      position: "HR-менеджер",
      text: "Как специалист по подбору персонала могу сказать, что резюме, созданные с помощью Yoffer, действительно выделяются. Они содержат все необходимые ключевые слова и хорошо структурированы.",
      avatar: "/img/testimonials/avatar4.jpg"
    }
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [touchStart, setTouchStart] = useState(0);
  const [touchEnd, setTouchEnd] = useState(0);
  const [isTouching, setIsTouching] = useState(false);
  const [translateX, setTranslateX] = useState(0);
  const sliderRef = useRef(null);

  const handleTouchStart = (e) => {
    setTouchStart(e.targetTouches[0].clientX);
    setIsTouching(true);
  };

  const handleTouchMove = (e) => {
    if (!isTouching) return;
    setTouchEnd(e.targetTouches[0].clientX);
    
    const diff = touchStart - e.targetTouches[0].clientX;
    const slideWidth = sliderRef.current?.offsetWidth || 0;
    
    // Calculate how far we've moved during the touch
    const newTranslate = -(currentIndex * 100) - (diff / slideWidth * 100);
    setTranslateX(newTranslate);
  };

  const handleTouchEnd = () => {
    setIsTouching(false);
    
    // Reset translateX to snap back to proper position
    setTranslateX(-(currentIndex * 100));
    
    if (touchStart - touchEnd > 75) {
      // Swipe left - go to next slide
      if (currentIndex < testimonials.length - 1) {
        setCurrentIndex(currentIndex + 1);
      }
    }
    
    if (touchStart - touchEnd < -75) {
      // Swipe right - go to previous slide
      if (currentIndex > 0) {
        setCurrentIndex(currentIndex - 1);
      }
    }
  };

  // Update translateX whenever currentIndex changes
  useEffect(() => {
    setTranslateX(-(currentIndex * 100));
  }, [currentIndex]);

  // Function to go to next slide
  const nextSlide = () => {
    if (currentIndex < testimonials.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  // Function to go to previous slide
  const prevSlide = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  return (
    <div className="testimonials-section">
      <div className="container">
        <h3 className="section-title">Отзывы наших пользователей</h3>
        
        <div className="testimonials-slider-container" ref={sliderRef}>
          <div 
            className="testimonials-slider"
            style={{ 
              transform: `translateX(${isTouching ? translateX : -(currentIndex * 100)}%)`,
              transition: isTouching ? 'none' : 'transform 0.3s ease-out'
            }}
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
          >
            {testimonials.map((testimonial) => (
              <div key={testimonial.id} className="testimonial-slide">
                <div className="testimonial-card">
                  <div className="testimonial-header">
                    <div className="testimonial-avatar">
                      <img src={testimonial.avatar} alt={testimonial.name} />
                    </div>
                    <div className="testimonial-author">
                      <h4>{testimonial.name}</h4>
                      <p className="testimonial-position">{testimonial.position}</p>
                    </div>
                  </div>
                  <p className="testimonial-text">{testimonial.text}</p>
                </div>
              </div>
            ))}
          </div>
          
          <div className="testimonials-nav">
            <button 
              className={`nav-button prev ${currentIndex === 0 ? 'disabled' : ''}`}
              onClick={prevSlide}
              disabled={currentIndex === 0}
            >
              &#10094;
            </button>
            <div className="slider-indicator">
              {testimonials.map((_, index) => (
                <span 
                  key={index} 
                  className={`indicator-dot ${index === currentIndex ? 'active' : ''}`}
                  onClick={() => setCurrentIndex(index)}
                />
              ))}
            </div>
            <button 
              className={`nav-button next ${currentIndex === testimonials.length - 1 ? 'disabled' : ''}`}
              onClick={nextSlide}
              disabled={currentIndex === testimonials.length - 1}
            >
              &#10095;
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TestimonialsSection;
