import React, { Suspense, lazy } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Header from './components/layout/Header';
import Footer from './components/layout/Footer';
import ErrorBoundary from './components/common/ErrorBoundary';
import LoadingSpinner from './components/common/LoadingSpinner';

// Lazy load sections for better performance
const HeroSection = lazy(() => import('./components/sections/Hero'));
// ...existing code...

// Main content component with error boundaries
const MainContent = () => (
  <ErrorBoundary fallback={<div className="error-container">Something went wrong. Please try again later.</div>}>
    <main className="main-content">
      <Suspense fallback={<LoadingSpinner />}>
        <HeroSection />
      </Suspense>

      <Suspense fallback={<LoadingSpinner />}>
        <FeaturesSection />
      </Suspense>

      <Suspense fallback={<LoadingSpinner />}>
        <PartnersSection />
      </Suspense>
      
      {/* ...остальные компоненты секций... */}
      
      <Suspense fallback={<LoadingSpinner />}>
        <TestimonialsSection />
      </Suspense>
    </main>
  </ErrorBoundary>
);

// ...оставшаяся часть компонента App...
