import React from 'react';
import './About.css';

const About = () => {
  return (
    <section className="about section" id="about">
      <div className="container">
        <div className="about-content">
          <div className="about-image">
            <img src="https://source.unsplash.com/random/600x400/?team" alt="Our Team" />
          </div>
          <div className="about-text">
            <h2 className="section-title">About Us</h2>
            <p>
              We are a passionate team of experts dedicated to creating the best solutions for our clients.
              Founded in 2020, our company has quickly grown to become a leader in the industry.
            </p>
            <p>
              Our mission is to provide innovative, user-friendly products that solve real-world problems.
              We believe in continuous improvement and staying ahead of the curve with the latest technologies.
            </p>
            <a href="#contact" className="btn btn-primary">Get in Touch</a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
