import React, { useState, useRef, useEffect } from 'react';
import './TestimonialsSection.css';

const TestimonialsSection = () => {
  // Sample testimonials data
  const testimonials = [
    {
      id: 1,
      name: "Анастасия",
      position: "UX/UI-дизайнер",
      title: "/img/testimonials/avatar2.jpg",
      text_before: "отправила свое резюме в 7 компаний, перезвонили только из одной. И те оказались мошенниками",
      text_after: "Послсделала резюме при помощи «Прозрачности» под 5 разных компаний. От 4-х из них получила приглашения на собеседования. В одной из них теперь работаю"
    }
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [touchStart, setTouchStart] = useState(0);
  const [touchEnd, setTouchEnd] = useState(0);
  const [isTouching, setIsTouching] = useState(false);
  const [translateX, setTranslateX] = useState(0);
  const sliderRef = useRef(null);

  const handleTouchStart = (e) => {
    setTouchStart(e.targetTouches[0].clientX);
    setIsTouching(true);
  };

  const handleTouchMove = (e) => {
    if (!isTouching) return;
    setTouchEnd(e.targetTouches[0].clientX);
    
    const diff = touchStart - e.targetTouches[0].clientX;
    const slideWidth = sliderRef.current?.offsetWidth || 0;
    
    // Calculate how far we've moved during the touch
    const newTranslate = -(currentIndex * 100) - (diff / slideWidth * 100);
    setTranslateX(newTranslate);
  };

  const handleTouchEnd = () => {
    setIsTouching(false);
    
    // Reset translateX to snap back to proper position
    setTranslateX(-(currentIndex * 100));
    
    if (touchStart - touchEnd > 75) {
      // Swipe left - go to next slide
      if (currentIndex < testimonials.length - 1) {
        setCurrentIndex(currentIndex + 1);
      }
    }
    
    if (touchStart - touchEnd < -75) {
      // Swipe right - go to previous slide
      if (currentIndex > 0) {
        setCurrentIndex(currentIndex - 1);
      }
    }
  };

  // Update translateX whenever currentIndex changes
  useEffect(() => {
    setTranslateX(-(currentIndex * 100));
  }, [currentIndex]);

  // Function to go to next slide
  const nextSlide = () => {
    if (currentIndex < testimonials.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  // Function to go to previous slide
  const prevSlide = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  return (
    <div className="testimonials-section">
      <div className="container">
        <h3 className="section-title">Отзывы наших пользователей</h3>
        
        <div className="testimonials-slider-container" ref={sliderRef}>
          <div 
            className="testimonials-slider"
            style={{ 
              transform: `translateX(${isTouching ? translateX : -(currentIndex * 100)}%)`,
              transition: isTouching ? 'none' : 'transform 0.3s ease-out'
            }}
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
          >
            {testimonials.map((testimonial) => (
              <div key={testimonial.id} className="testimonial-slide">
                <div className="testimonial-card">
                  <div className="testimonial-header">
                   
                    <div className="testimonial-author">
                        
                      <h4>{testimonial.name}</h4>
                      
                      <p className="testimonial-position">{testimonial.position}</p>
                    </div>
                    
                  </div>
                  <div className="testimonial-title">
                        <p className="testimonial-title">{testimonial.title}</p>
                    </div>
                  <p className="testimonial-text"><strong>До: </strong>{testimonial.text_before}</p>
                  <p className="testimonial-text"><strong>После: </strong>{testimonial.text_after}</p>
                </div>
              </div>
            ))}
          </div>
          
          
        </div>
      </div>
    </div>
  );
};

export default TestimonialsSection;
