import React, { useState, useRef, useEffect } from 'react';
import './DreamJobSection.css';

const DreamJobSection = () => {
  // Sample cards data
  const cards = [
    {
      title: 'Экономия времени',
      description: 'Создание резюме занимает всего 15 минут'
    },
    {
      title: 'Релевантность',
      description: 'Резюме идеально соответствует требованиям вакансии'
    },
    {
      title: 'Персонализация',
      description: 'Индивидуальный подход к каждой позиции'
    },
    {
      title: 'Ключевые слова',
      description: 'Оптимизация под системы отслеживания резюме'
    },
    {
      title: 'Конкурентное преимущество',
      description: 'Выделяйтесь среди других кандидатов'
    },
    {
      title: 'Профессиональный подход',
      description: 'Создано с учетом отраслевых стандартов'
    }
  ];

  const [touchStart, setTouchStart] = useState(0);
  const [touchEnd, setTouchEnd] = useState(0);
  const [isTouching, setIsTouching] = useState(false);
  const [scrollX, setScrollX] = useState(0);
  const sliderRef = useRef(null);

  const handleTouchStart = (e) => {
    setTouchStart(e.targetTouches[0].clientX);
    setIsTouching(true);
  };

  const handleTouchMove = (e) => {
    if (!isTouching) return;
    setTouchEnd(e.targetTouches[0].clientX);
    
    const diff = touchStart - e.targetTouches[0].clientX;
    const currentScroll = sliderRef.current?.scrollLeft || 0;
    
    // Update scroll position during touch move
    sliderRef.current.scrollLeft = currentScroll + diff;
    setScrollX(currentScroll + diff);
    setTouchStart(e.targetTouches[0].clientX);
  };

  const handleTouchEnd = () => {
    setIsTouching(false);
  };

  return (
    <div className="dream-job-section">
      <div className="container">
        <h3 className="section-title">Как получить работу мечты?</h3>
        
        <div className="dream-job-intro">
          <p>Чтобы ускорить поиск работы, увеличить количество откликов от работодателей, а также претендовать на более высокую оплату, создавайте отдельное резюме под каждую вакансию</p>
        </div>
        
        <h3 className="section-subtitle">Поручите это нам</h3>
        
        <div 
          className="cards-slider"
          ref={sliderRef}
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
        >
          {cards.map((card, index) => (
            <div className="algorithm-card" key={index}>
              <h4 className="card-title">{card.title}</h4>
              <p className="card-description">{card.description}</p>
            </div>
          ))}
        </div>
        
        <div className="dream-job-action">
          <button className="button button-primary button-large">Создать резюме</button>
        </div>
      </div>
    </div>
  );
};

export default DreamJobSection;
