import React from 'react';
import './Algorithm.css';
import Section from '../common/Section';
import Button from '../common/Button';

const Algorithm = () => {
  // Sample algorithm steps data
  const algorithmSteps = [
    {
      id: 1,
      title: "Analysis",
      description: "We analyze your experience, skills, and career goals to understand your unique profile."
    },
    {
      id: 2,
      title: "Strategy",
      description: "Based on analysis, we develop a personalized strategy to highlight your strengths."
    },
    {
      id: 3,
      title: "Optimization",
      description: "We optimize your resume structure and content to pass ATS systems and attract recruiters."
    },
    {
      id: 4,
      title: "Design",
      description: "Your resume gets a professional design that stands out while maintaining industry standards."
    },
    {
      id: 5,
      title: "Review",
      description: "We thoroughly review and refine your resume to ensure it's polished and error-free."
    },
    {
      id: 6,
      title: "Delivery",
      description: "You receive your finalized resume along with tips for how to best use it in your job search."
    }
  ];

  return (
    <section className="algorithm-container">
      <div className="container">
        <h3 className="section-title accent">Алгоритм обучен профессиональными рекрутерами и HR</h3>
      </div>
      
      <div className="cards-flex">
        <div className="cards-container">
          {/* First set of cards */}
          {algorithmSteps.map((step) => (
            <div key={`step-${step.id}`} className="algorithm-card">
         
              <p>{step.title}</p>
              <div className='like'>
                <img src="/img/svg/like.svg" alt="" />
              </div>
            </div>
          ))}
          
          {/* Duplicate set for seamless infinite scrolling */}
          {algorithmSteps.map((step) => (
            <div key={`step-dup-${step.id}`} className="algorithm-card">
            
              <p>{step.title}</p>
              <div className='like'>
                <img src="/img/svg/like.svg" alt="" />
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Algorithm;
