import React, { useEffect, useState, useRef } from 'react';
import Section from '../common/Section';
import Button from '../common/Button';
import './Partners.css';

const Partners = () => {
  const [logos, setLogos] = useState([]);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);
  const containerRef = useRef(null);
  
  useEffect(() => {
    // Dynamically import all logo files from the directory
    const importAllLogos = () => {
      let logoContext;
      try {
        // For webpack environments
        logoContext = require.context('../../../public/img/logos', false, /\.(png|jpe?g|svg)$/);
        return logoContext.keys().map(logoContext);
      } catch (error) {
        console.error('Error loading logos:', error);
        
        // Fallback to some sample logos if require.context is not available
        return [
          '/img/logos/logo1.png',
          '/img/logos/logo2.png',
          '/img/logos/logo3.png',
        ].map(path => ({ default: path }));
      }
    };
    
    const importedLogos = importAllLogos();
    // Extract the file paths from the imported modules
    const logoPaths = importedLogos.map(module => 
      typeof module === 'string' ? module : module.default
    );
    
    setLogos(logoPaths);
  }, []);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX - containerRef.current.offsetLeft);
    setScrollLeft(containerRef.current.scrollLeft);
  };

  const handleTouchStart = (e) => {
    setIsDragging(true);
    setStartX(e.touches[0].pageX - containerRef.current.offsetLeft);
    setScrollLeft(containerRef.current.scrollLeft);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const x = e.pageX - containerRef.current.offsetLeft;
    const walk = (x - startX) * 2; // Scroll speed multiplier
    
    // Apply manual transform during drag
    containerRef.current.style.transform = `translateX(${walk}px)`;
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const x = e.touches[0].pageX - containerRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    
    containerRef.current.style.transform = `translateX(${walk}px)`;
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    // Reset transform to let the animation take over again
    setTimeout(() => {
      if (containerRef.current) {
        containerRef.current.style.transform = '';
      }
    }, 100);
  };

  const handleMouseLeave = () => {
    if (isDragging) {
      handleMouseUp();
    }
  };

  return (
    <section className="partners">
       <div className='container'>
       <h3 className='section-title'> С нашими резюме люди устраиваются в лучшие компании</h3>
       </div>
      <div className="logo-flex"
           onMouseDown={handleMouseDown}
           onMouseMove={handleMouseMove}
           onMouseUp={handleMouseUp}
           onMouseLeave={handleMouseLeave}
           onTouchStart={handleTouchStart}
           onTouchMove={handleTouchMove}
           onTouchEnd={handleMouseUp}>
        <div className={`logo-container ${isDragging ? 'dragging' : ''}`}
             ref={containerRef}>
          {/* First set of logos */}
          {logos.map((logo, index) => (
            <div key={`logo-${index}`} className="logo">
              <img src={logo} alt={`Partner logo ${index + 1}`} />
            </div>
          ))}
          
          {/* Duplicate set for seamless infinite scrolling */}
          {logos.map((logo, index) => (
            <div key={`logo-dup-${index}`} className="logo">
              <img src={logo} alt={`Partner logo ${index + 1}`} />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Partners;
